mapper.select:
    select 
    	t1.*,t2.productCount
    from t_mornsun_brand t1

    	INNER JOIN (
		SELECT brand_id,count(1) as productCount from t_mornsun_product 
		where isavailable=1 and state=1

		and catalog_id = #{catalogId,jdbcType=VARCHAR}

		GROUP BY brand_id 
		) t2 on t1.id=t2.brand_id and t2.productCount>0


	1=1

	and t1.brand_name = #{brandName,jdbcType=VARCHAR}


	and t1.brand_name_en = #{brandNameEn,jdbcType=VARCHAR}


	and t1.state = #{state,jdbcType=CHAR}


	and t1.isavailable = #{isavailable,jdbcType=CHAR}



		order by CONVERT( t1.brand_name USING gbk ) COLLATE gbk_chinese_ci asc,
		CONVERT( t1.brand_name_en USING gbk ) COLLATE gbk_chinese_ci asc

select
from t_mornsun_brand
1=1
and brand_name like CONCAT('%',#{brandName,jdbcType=VARCHAR},'%')
and brand_name_en like CONCAT('%',#{brandNameEn,jdbcType=VARCHAR},'%')
and brand_url = #{brandUrl,jdbcType=VARCHAR}
and state = #{state,jdbcType=CHAR}
and state = 1
and isavailable = #{isavailable,jdbcType=CHAR}
order by CONVERT( brand_name USING gbk ) COLLATE gbk_chinese_ci asc, CONVERT( brand_name_en USING gbk ) COLLATE gbk_chinese_ci asc

    select 

    from t_mornsun_brand

	1=1

	and brand_name = #{brandName,jdbcType=VARCHAR}


	and brand_name_en = #{brandNameEn,jdbcType=VARCHAR}


	and brand_url = #{brandUrl,jdbcType=VARCHAR}


	and state = #{state,jdbcType=CHAR}


	and isavailable = #{isavailable,jdbcType=CHAR}

mapper.delete:
    delete from t_mornsun_brand

	1=1

	and brand_name = #{brandName,jdbcType=VARCHAR}


	and brand_name_en = #{brandNameEn,jdbcType=VARCHAR}


	and brand_url = #{brandUrl,jdbcType=VARCHAR}


	and state = #{state,jdbcType=CHAR}


	and isavailable = #{isavailable,jdbcType=CHAR}

mapper.select:
    select count(*) from t_mornsun_brand

	1=1

	and brand_name = #{brandName,jdbcType=VARCHAR}


	and brand_name_en = #{brandNameEn,jdbcType=VARCHAR}


	and brand_url = #{brandUrl,jdbcType=VARCHAR}


	and state = #{state,jdbcType=CHAR}


	and isavailable = #{isavailable,jdbcType=CHAR}
